import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChevronLeft, faSignOut, faHome, faUsers, faUser, faCalendar,
  faFileInvoice, faFileText, faTasks, faFileInvoiceDollar, faMoneyCheck
} from "@fortawesome/free-solid-svg-icons";
import { Link, useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import "../index.css";

const Cotizacion = () => {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [cliente, setCliente] = useState("");
  const [fecha, setFecha] = useState("");
  const [estado, setEstado] = useState("");
  const [servicios, setServicios] = useState([]);
  const [serviciosSeleccionados, setServiciosSeleccionados] = useState({});
  const [clientes, setClientes] = useState([]);


  const navigate = useNavigate();
  const emailUsuario = localStorage.getItem('email');

  useEffect(() => {
    fetch('http://localhost:8081/servicios')
      .then(res => res.json())
      .then(data => setServicios(data))
      .catch(error => console.error('Error al cargar servicios:', error));

  fetch('http://localhost:8081/clientes')
    .then(res => res.json())
    .then(data => {
      const clientesActivos = data.filter(cliente => cliente.estado === 'activo');
      setClientes(clientesActivos);
    })
    .catch(err => console.error('Error al cargar clientes:', err));

    const hoy = new Date();
    const dia = String(hoy.getDate()).padStart(2, '0');
    const mes = String(hoy.getMonth() + 1).padStart(2, '0');
    const anio = hoy.getFullYear();
    const fechaActual = `${anio}-${mes}-${dia}`;

    if (!fecha) {
      setFecha(fechaActual);
    }
  }, []);

  const toggleSidebar = () => setSidebarCollapsed(!sidebarCollapsed);

  const cerrarSesion = () => {
    localStorage.clear();
    sessionStorage.clear();
    navigate('/iniciarsesion', { replace: true });
    window.history.pushState(null, '', '/iniciarsesion');
    window.onpopstate = () => {
      window.history.go(1);
    };
  };

  const manejarSeleccion = (id_servicio) => {
    setServiciosSeleccionados(prev => ({
      ...prev,
      [id_servicio]: !prev[id_servicio]
    }));
  };

  const subtotal = servicios
    .filter(s => serviciosSeleccionados[s.id_servicio])
    .reduce((acc, s) => acc + Number(s.costo_base || 0), 0);

  const impuesto = subtotal * 0.18;
  const total = subtotal + impuesto;

  const guardarCotizacion = async () => {
    const datosCotizacion = {
      id_cliente: Number(cliente),
      fecha,
      estado,
      servicios: Object.keys(serviciosSeleccionados)
        .filter(id => serviciosSeleccionados[id])
        .map(id => {
          const servicio = servicios.find(s => s.id_servicio === Number(id));
          return {
            id_servicio: Number(id),
            costo_base: Number(servicio.costo_base)
          };
        }),
      subtotal,
      impuesto,
      total
    };

    try {
      const response = await fetch('http://localhost:8081/cotizaciones', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(datosCotizacion)
      });

      if (!response.ok) {
        throw new Error("Error al guardar la cotización.");
      }

      return true;
    } catch (error) {
      console.error('Error al guardar la cotización:', error);
      alert("Error en la conexión al guardar la cotización.");
      return false;
    }
  };

  const generarPDF = () => {
    const doc = new jsPDF();

    doc.setFontSize(18);
    doc.text("Servicios Múltiples Pérez", 14, 20);
    doc.text("Cotización de Servicios", 14, 29);

    const clienteSeleccionado = clientes.find(c => c.id_cliente === Number(cliente));
    let fechaFormateada = fecha;
    if (fecha) {
      const [anio, mes, dia] = fecha.split("-");
      fechaFormateada = `${dia}/${mes}/${anio}`;
    }

    const logoBase64 = 'data:image/jpeg;base64,';
    doc.addImage(logoBase64, 'JPEG', 140, 15, 40, 40);
    doc.setFontSize(12);
    doc.text(`Cliente: ${clienteSeleccionado ? clienteSeleccionado.nombre : ''}`, 14, 35);
    doc.text(`Fecha: ${fechaFormateada}`, 14, 43);
    doc.text(`Estado: ${estado}`, 14, 51);

    const serviciosTabla = servicios
      .filter(s => serviciosSeleccionados[s.id_servicio])
      .map(s => [
        s.nombre_servicio,
        `$${Number(s.costo_base).toFixed(2)}`
      ]);

    autoTable(doc, {
      startY: 60,
      head: [["Servicio", "Precio"]],
      body: serviciosTabla,
      headStyles: {
        fillColor: '#040293',
        textColor: 255,
        fontStyle: 'bold'
      }
    });

    const finalY = doc.lastAutoTable.finalY || 80;

    doc.text(`Subtotal: $${subtotal.toFixed(2)}`, 14, finalY + 10);
    doc.text(`Impuesto (18%): $${impuesto.toFixed(2)}`, 14, finalY + 18);
    doc.text(`Total: $${total.toFixed(2)}`, 14, finalY + 26);


    doc.setFontSize(12);
    doc.setFont("helvetica", "bold");
    doc.setTextColor('#040293');

    doc.text("Gracias por elegirnos", 105, finalY + 40, { align: "center" });
    doc.text("*Estos precios pueden variar dependiendo la complejidad del trabajo*", 105, finalY + 50, { align: "center" });

    doc.save(`cotizacion_${clienteSeleccionado ? clienteSeleccionado.nombre : 'cliente'}.pdf`);
  };

  const handleGuardar = async (e) => {
    e.preventDefault();
    const guardado = await guardarCotizacion();
    if (guardado) {
      alert("Cotización guardada correctamente.");
      navigate('/vercotizaciones');
    }
  };

  const handleEnviar = async (e) => {
    e.preventDefault();
    const guardado = await guardarCotizacion();
    if (guardado) {
      generarPDF();
      alert("Cotización guardada y PDF generado.");
    }
  };

  return (
    <div className="dashboard">
      <div className={`sidebar ${sidebarCollapsed ? "collapsed" : ""}`}>
        <h2>Bienvenido</h2>
        <p className="subtexto-email">{emailUsuario}</p>
        <ul>
          <li><Link to="/dashboard"><FontAwesomeIcon icon={faHome} /> <span>Inicio</span></Link></li>
          <li><Link to="/clienteempleado"><FontAwesomeIcon icon={faUsers} /> <span>Clientes</span></Link></li>
          <li><Link to="/empleado"><FontAwesomeIcon icon={faUser} /> <span>Empleados</span></Link></li>
          <li><Link to="/solicitudservicio"><FontAwesomeIcon icon={faFileText} /> <span>Solicitud servicio</span></Link></li>
          <li><Link to="/formulariocita"><FontAwesomeIcon icon={faCalendar} /> <span>Citas</span></Link></li>
          <li><Link to="/registrotrabajo"><FontAwesomeIcon icon={faTasks} /> <span>Registro trabajo</span></Link></li>
          <li><Link to="/vercotizaciones"><FontAwesomeIcon icon={faFileInvoice} /> <span>Cotización</span></Link></li>
          <li><Link to="/factura"><FontAwesomeIcon icon={faFileInvoiceDollar} /> <span>Factura</span></Link></li>
          <li><Link to="/pago"><FontAwesomeIcon icon={faMoneyCheck} /> <span>Pagos</span></Link></li>
        </ul>
        <ul>
          <li className="Cerrarsesion">
            <button onClick={cerrarSesion} className="logout-button">
              <FontAwesomeIcon icon={faSignOut} /> <span>Cerrar sesión</span>
            </button>
          </li>
        </ul>
        <button className="toggle-btn" onClick={toggleSidebar}>
          <FontAwesomeIcon icon={faChevronLeft} />
        </button>
      </div>

      <div className="dashboard-content">
        <Link to="/vercotizaciones" className="boton-retroceso">
          <FontAwesomeIcon icon={faChevronLeft} />
        </Link>
        <h2>Bienvenido a la sección de cotización</h2>

        <div className="invoice-container">
          <div className="invoice-card">
            <div className="invoice-grid">
              <div className="input-group">
                <label>Cliente</label>
                <select className="campo-cita" value={cliente} onChange={(e) => setCliente(e.target.value)} required>
                  <option value="">Selecciona un cliente</option>
                  {clientes.map((c) => (
                    <option key={c.id_cliente} value={c.id_cliente}>{c.nombre}</option>
                  ))}
                </select>
              </div>
              <div className="input-group">
                <label>Fecha</label>
                <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} />
              </div>
              
            </div>

            <h3>Servicios disponibles</h3>
            <table className="invoice-table">
              <thead>
                <tr>
                  <th>Seleccionar</th>
                  <th>Servicio</th>
                  <th>Precio base</th>
                </tr>
              </thead>
              <tbody>
                {servicios.length > 0 ? (
                  servicios.map((s) => (
                    <tr key={s.id_servicio}>
                      <td>
                        <input
                          type="checkbox"
                          checked={!!serviciosSeleccionados[s.id_servicio]}
                          onChange={() => manejarSeleccion(s.id_servicio)}
                        />
                      </td>
                      <td>{s.nombre_servicio}</td>
                      <td>${Number(s.costo_base).toFixed(2)}</td>
                    </tr>
                  ))
                ) : (
                  <tr><td colSpan="3">No hay servicios disponibles</td></tr>
                )}
              </tbody>
            </table>

            <div className="subtotal">Subtotal: ${subtotal.toFixed(2)}</div>
          </div>

          <div className="invoice-summary">
            <h2>Suma</h2>
            <div className="summary-row"><span>Subtotal</span><span>${subtotal.toFixed(2)}</span></div>
            <div className="summary-row"><span>Imp (18%)</span><span>${impuesto.toFixed(2)}</span></div>
            <div className="summary-total"><span>Total</span><span>${total.toFixed(2)}</span></div>
            <div className="button-group">
              <button onClick={handleGuardar} className="save">Guardar</button>
              <button onClick={handleEnviar} className="send">Enviar</button>
              <button className="cancel" onClick={() => navigate('/vercotizaciones')}>
                Cancelar
              </button>

            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cotizacion;
